import React, { useState } from 'react';
import axios from 'axios';

function App() {
  // State to store selected file and result
  const [selectedFile, setSelectedFile] = useState(null);
  const [result, setResult] = useState(null);

  // Handle file input change
  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if file is selected
    if (!selectedFile) {
      alert("Please select a file first!");
      return;
    }

    // Prepare form data to send the image to the Flask API
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      // Make a POST request to the Flask API with the image
      const response = await axios.post('http://localhost:5000/predict', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Set the result from the API response
      setResult(response.data);
    } catch (error) {
      console.error("There was an error with the API request", error);
      alert("Error uploading file or getting prediction");
    }
  };

  return (
    <div className="App">
      <h1>Road Anomaly Detection</h1>

      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} accept="image/*" />
        <button type="submit">Upload and Predict</button>
      </form>

      {result && (
        <div>
          <h2>Prediction Result</h2>
          <p>{result.message}</p>
          <p><strong>Accident Type:</strong> {result.accident_type}</p>
        </div>
      )}
    </div>
  );
}

export default App;
